const post = $input.first().json;

const titulo = post.post_title || post.title?.rendered || '';
const link = post.guid || post.link || '';
const excerpt = (post.post_excerpt || post.excerpt?.rendered || '').replace(/<[^>]*>/g, '').substring(0, 200);
const featuredImage = post.featured_image_url || post._embedded?.['wp:featuredmedia']?.[0]?.source_url || '';
const categories = (post.categories_names || []);
const editoria = categories[0] || 'Noticias';
const autor = post.author_name || 'Redacao';
const tags = (post.tags_names || []);
const tipo = (post.post_type_label || 'noticia').toLowerCase();

const hashtags = ['EspacoDoPovo', 'Periferia', ...categories, ...tags]
  .map(t => '#' + t.replace(/[^a-zA-Z0-9\u00C0-\u024F]/g, ''))
  .filter((v, i, a) => a.indexOf(v) === i)
  .slice(0, 12)
  .join(' ');

return [{
  json: {
    titulo, link, excerpt, featuredImage, editoria, autor, tipo,
    categories: categories.join(', '),
    hashtags,
    post_id: post.ID || post.id
  }
}];

const d = $input.first().json;

// === CONSTANTES DA MARCA ===
const BRAND = {
  nome: 'ESPA√áO DO POVO',
  sub: 'JORNAL COMUNIT√ÅRIO',
  handle: '@espacodopovo',
  site: 'www.espacodopovo.com.br',
  cor: '#c0392b',
  lime: '#CCFF00',
  logoSvg: '<svg viewBox=\"0 0 24 24\" fill=\"#0A0A0A\"><path d=\"M12 2 L14 9 L21 10 L14 11 L12 18 L10 11 L3 10 L10 9 Z\"/></svg>'
};

const CORES = {
  'URGENTE':'#e74c3c','POLITICA':'#2c3e50','SAUDE':'#27ae60','EDUCACAO':'#2980b9',
  'TRANSPORTE':'#e67e22','CULTURA':'#8e44ad','ESPORTE':'#c0392b','HISTORIA':'#d4a017',
  'SERVICOS':'#16a085','GASTRONOMIA':'#d35400','EVENTOS':'#8e44ad',
  'MEIO AMBIENTE':'#27ae60','EMPREENDEDORISMO':'#2c3e50','NOTICIAS':'#c0392b',
  'CURIOSIDADE':'#f39c12','EDITORIAL':'#1a1a2e','COLUNA':'#34495e'
};

const titulo = d.titulo || 'Titulo';
const editoria = (d.editoria || 'Noticias').toUpperCase();
const autor = d.autor || 'Redacao';
const img = d.featuredImage || '';
const cor = CORES[editoria] || '#c0392b';
const isUrgente = editoria === 'URGENTE';
const isColuna = d.tipo === 'coluna' || d.tipo === 'opiniao' || editoria === 'COLUNA' || editoria === 'EDITORIAL';
const dataStr = new Date().toLocaleDateString('pt-BR', { day:'2-digit', month:'short', year:'numeric' });

function breakLines(text, max) {
  const words = text.split(' ');
  let lines = [], cur = '';
  for (const w of words) {
    if ((cur + ' ' + w).trim().length > max) { if (cur) lines.push(cur.trim()); cur = w; }
    else cur = (cur + ' ' + w).trim();
  }
  if (cur) lines.push(cur.trim());
  if (lines.length > 5) { lines = lines.slice(0,5); lines[4] = lines[4].substring(0,max-3)+'...'; }
  return lines;
}

// Gera card para cada formato
function genCard(w, h, fmt) {
  const isSq = fmt === 'square';
  const lines = breakLines(titulo, isSq ? 24 : 30);
  const fs = lines.length <= 2 ? (isSq?52:44) : lines.length <= 3 ? (isSq?44:38) : (isSq?38:34);
  const pad = isSq ? 44 : 36;

  // Selecao de estilo do card baseado no tipo
  let bgStyle, overlayStyle, contentAlign, titleFont, extraContent;

  if (isUrgente) {
    bgStyle = `background:linear-gradient(135deg,#1a0000 0%,#3d0000 30%,#8b0000 100%);`;
    overlayStyle = img ? `background:url('${img}') center/cover; opacity:0.15;` : '';
    contentAlign = 'center';
    titleFont = `font-size:${fs+4}px;`;
    extraContent = '';
  } else if (isColuna) {
    bgStyle = `background:linear-gradient(160deg,#0f0f1a 0%,#1a1a2e 40%,#16213e 100%);`;
    overlayStyle = '';
    contentAlign = 'center';
    titleFont = `font-size:${fs}px; font-style:italic;`;
    extraContent = `<div style="display:flex;align-items:center;gap:14px;justify-content:center;margin-top:20px;"><div style="width:52px;height:52px;border-radius:50%;border:3px solid ${cor};background:${cor};display:flex;align-items:center;justify-content:center;color:white;font-weight:800;font-size:20px;">${(autor||'R')[0]}</div><div style="text-align:left;"><div style="color:white;font-size:${isSq?16:14}px;font-weight:700;">${autor}</div><div style="color:rgba(255,255,255,0.5);font-size:${isSq?12:11}px;">Colunista</div></div></div>`;
  } else if (!img) {
    bgStyle = `background:linear-gradient(160deg,#0a0a14 0%,${cor}22 50%,#0a0a14 100%);`;
    overlayStyle = '';
    contentAlign = 'left';
    titleFont = `font-size:${fs}px;`;
    extraContent = '';
  } else {
    bgStyle = `background:#0a0a0a;`;
    overlayStyle = `background:url('${img}') center/cover; opacity:0.45;`;
    contentAlign = 'left';
    titleFont = `font-size:${fs}px;`;
    extraContent = `<div style="color:rgba(255,255,255,0.75);font-size:${isSq?15:13}px;font-weight:600;margin-top:12px;">Por ${autor}</div>`;
  }

  return `<!DOCTYPE html><html><head><meta charset="UTF-8"><style>
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700;800;900&display=swap');
*{margin:0;padding:0;box-sizing:border-box;}
body{width:${w}px;height:${h}px;overflow:hidden;font-family:'Inter',sans-serif;}
.card{width:${w}px;height:${h}px;position:relative;${bgStyle}display:flex;flex-direction:column;justify-content:space-between;}
.bg{position:absolute;top:0;left:0;right:0;bottom:0;${overlayStyle}}
.bg::after{content:'';position:absolute;bottom:0;left:0;right:0;height:70%;background:linear-gradient(to top,rgba(0,0,0,0.95) 0%,rgba(0,0,0,0.5) 40%,transparent 100%);}
.card>*{position:relative;z-index:1;}
.top{display:flex;justify-content:space-between;align-items:flex-start;padding:${pad}px;}
.logo{display:flex;align-items:center;gap:12px;}
.logo-i{width:${isSq?50:44}px;height:${isSq?50:44}px;background:${BRAND.lime};border-radius:50%;display:flex;align-items:center;justify-content:center;}
.logo-i svg{width:${isSq?26:22}px;height:${isSq?26:22}px;}
.logo-t{color:white;font-weight:800;font-size:${isSq?16:14}px;line-height:1.15;text-transform:uppercase;letter-spacing:1px;}
.logo-t span{display:block;font-size:${isSq?9:8}px;font-weight:600;opacity:0.5;letter-spacing:2.5px;margin-top:2px;}
.badge{background:${isUrgente?'white':cor};color:${isUrgente?BRAND.cor:'white'};padding:8px 20px;border-radius:4px;font-size:${isSq?13:11}px;font-weight:800;letter-spacing:3px;text-transform:uppercase;}
.cnt{flex:1;display:flex;flex-direction:column;justify-content:${contentAlign==='center'?'center':'flex-end'};${contentAlign==='center'?'align-items:center;text-align:center;':''}padding:0 ${pad+16}px ${isSq?20:12}px;}
.tit{color:white;${titleFont}font-weight:900;line-height:1.18;max-width:${isSq?920:1020}px;text-shadow:0 2px 30px rgba(0,0,0,0.7);}
.acc{width:60px;height:4px;background:${cor};border-radius:2px;margin-top:${isSq?18:12}px;${contentAlign==='center'?'margin-left:auto;margin-right:auto;':''}}
.bot{display:flex;justify-content:space-between;align-items:center;padding:${isSq?'18px '+pad+'px 28px':'14px '+pad+'px 20px'};border-top:1px solid rgba(255,255,255,0.08);}
.handle{color:rgba(255,255,255,0.5);font-size:${isSq?15:13}px;font-weight:600;}
.url{color:${cor};font-size:${isSq?14:12}px;font-weight:700;}
</style></head><body>
<div class="card">
  <div class="bg"></div>
  <div class="top">
    <div class="logo"><div class="logo-i">${BRAND.logoSvg}</div><div class="logo-t">${BRAND.nome}<span>${BRAND.sub}</span></div></div>
    <div class="badge">${isUrgente?'URGENTE':editoria}</div>
  </div>
  <div class="cnt">
    ${isColuna?'<div style="font-size:70px;color:'+cor+';opacity:0.35;font-family:Georgia,serif;line-height:0.5;margin-bottom:14px;">&ldquo;</div>':''}
    <div class="tit">${lines.join('<br>')}</div>
    <div class="acc"></div>
    ${extraContent}
  </div>
  <div class="bot">
    <div class="handle">${BRAND.handle}</div>
    <div class="url">${BRAND.site}</div>
  </div>
</div>
</body></html>`;
}

// Gera os 2 formatos
const cardSquareHtml = genCard(1080, 1080, 'square');
const cardWideHtml = genCard(1200, 630, 'wide');

return [{ json: { ...d, cardSquareHtml, cardWideHtml } }];

const squareUpload = $('Upload Card Quadrado (WP)').first().json;
const wideUpload = $('Upload Card Wide (WP)').first().json;
const postData = $('Extrair Dados do Post').first().json;

const squareUrl = squareUpload.source_url || squareUpload.guid?.rendered || '';
const wideUrl = wideUpload.source_url || wideUpload.guid?.rendered || '';

// Textos otimizados por canal
const fbText = `${postData.titulo}\n\n${postData.excerpt}...\n\n${postData.hashtags}`;

const instaCaption = `${postData.titulo}\n\n${postData.excerpt}...\n\nüì∞ Link na bio!\n\n${postData.hashtags}`;

const telegramText = `üì∞ *${postData.titulo}*\n_${postData.editoria}_ | Por *${postData.autor}*\n\n${postData.excerpt}...\n\nüîó [Leia completo](${postData.link})\n\n${postData.hashtags}`;

const whatsappText = `üì∞ *${postData.titulo}*\n_${postData.editoria} | ${postData.autor}_\n\n${postData.excerpt}...\n\nüëâ ${postData.link}`;

return [{
  json: {
    ...postData,
    squareUrl,
    wideUrl,
    fbText,
    instaCaption,
    telegramText,
    whatsappText
  }
}];

